import React from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CourseForStudent from "../components/CourseForStudent";

function Student() {
  const [idCourse, setidCourse] = useState("");
  const [courses, setCourses] = useState([]); 
  const[chosenCourse, setChosenCourse] = useState("");
  const[chosenTask, setChosenTask] = useState("");
  const navigate = useNavigate();
  const logOut = () => {
    navigate(-1);
  };
  const updateCourses = () => {
    fetch("http://localhost:8080/studentCourses", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        idStudent: sessionStorage.getItem("idStudent"),
      }),
    })
      .then((res) => res.json())
      .then((data) => setCourses(data));
  };
  useEffect(() => {
    updateCourses();
  }, []);
  const handle = (event) => {
    setidCourse(event.target.value);
  };
  const handleAddCourse = () => {
    fetch("http://localhost:8080/addCourse", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        idCourse: idCourse,
        idStudent: sessionStorage.getItem("idStudent"),
      }),
    }).then(() => updateCourses());
  };
  return (
    <div>
      <h1>Student: {sessionStorage.getItem("idStudent")}</h1>
      <div style={{ display: "flex", justifyContent: "space-around" }}>
        <div>
        <p>Courses({courses.length})</p>
          {/* <div>
            list
            <br />
            of <br />
            courses <br />
          </div> */}
           <div>
          <div>
            {courses.map((course, index) => (
              <CourseForStudent course={course} index={index} setChosenCourse={setChosenCourse} setChosenTask={setChosenTask}/>
            ))}
          </div>
        </div>
        </div>
        <div>
          <div
            style={{
              display: " flex",
              flexDirection: "column",
              justifyContent: "center",
              paddingTop: " 40px",
            }}
          >
            <Button
              variant="contained"
              style={{ background: "#010101", marginBottom: "10px" }}
              onClick={handleAddCourse}
            >
              Add course
            </Button>
            <TextField
              label="Enter name"
              variant="outlined"
              value={idCourse}
              onChange={handle}
            />
          </div>
          <div
            style={{
              display: " flex",
              flexDirection: "column",
              justifyContent: "center",
              paddingTop: " 40px",
            }}
          >
            <Button
              variant="contained"
              style={{ background: "#010101", marginBottom: "10px" }}
              onClick={handleClick}
            >
              Complete task
            </Button>
          </div>
        </div>
      </div>
      <Button
        label="Log out"
        variant="contained"
        style={{ background: "#010101", marginTop: "40px" }}
        onClick={logOut}
      >
        Log out
        </Button>
    </div>
  );
}

export default Student;
