import React from "react";

function ListItem({ course, index }) {
  return (
    <div
      key={index}
      style={{ display: "flex", gap: "5px", alignItems: "baseline" }}
    >
      <button
        style={{
          height: "50%",
          background: "rgb(1, 1, 1)",
          marginBottom: "10px",
          borderRadius: "2px",
          border: "none",
          color: "#fff",
        }}
        onClick={() => {
          const details = document.getElementById(`details-${index}`);
          if (details.style.display === "none") {
            details.style.display = "block";
          } else {
            details.style.display = "none";
          }
        }}
      >
        +
      </button>
      <p>
        {index + 1}. {course.name}
      </p>
      <div
        id={`details-${index}`}
        style={{ display: "none", textAlign: "left", paddingLeft: "10px" }}
      >
        <p>Teacher: {course.teacher.id}</p>
        <p style={{ display: "flex", flexDirection: "column" }}>
          <button
            style={{
              height: "50%",
              background: "rgb(1, 1, 1)",
              marginBottom: "10px",
              borderRadius: "2px",
              border: "none",
              color: "#fff",
            }}
            onClick={() => {
              const details = document.getElementById(`details-${index}`);
              if (details.style.display === "none") {
                details.style.display = "block";
              } else {
                details.style.display = "none";
              }
            }}
          >
            +
          </button>
          Tasks:{" "}
          {course.tasks.map((task) => (
            <p>task</p>
          ))}
        </p>
        <p style={{ display: "flex", flexDirection: "column" }}>
          <button
            style={{
              height: "50%",
              background: "rgb(1, 1, 1)",
              marginBottom: "10px",
              borderRadius: "2px",
              border: "none",
              color: "#fff",
            }}
            onClick={() => {
              const details = document.getElementById(`students-details${index}`);
              if (details.style.display === "none") {
                details.style.display = "block";
              } else {
                details.style.display = "none";
              }
            }}
          >
            +
          </button>
          Students:{" "}
          {course.students.map((student) => (
            <p>student.id</p>
          ))}
        </p>
      </div>
    </div>
  );
}

export default ListItem;
