import React from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import CourseForStudent from "../components/CourseForStudent";
import { Modal } from "@mui/material";

function Student() {
  const [courseId, setCourseId] = useState("");
  const [courses, setCourses] = useState([]); 
  const[chosenCourse, setChosenCourse] = useState("");
  const[chosenTask, setChosenTask] = useState("");
  const [open, setOpen] = useState(false);
  const navigate = useNavigate();
  console.log(courses)
  const logOut = () => {
    navigate(-1);
  };
  const updateCourses = () => {
    fetch("http://localhost:8080/studentCourses", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        idStudent: sessionStorage.getItem("idStudent"),
      }),
    })
      .then((res) => res.json())
      .then((data) => setCourses(data));
  };

  useEffect(() => {
    updateCourses();
  }, []);
  const handleCourseId = (event) => {
    setCourseId(event.target.value);
  };
  const handleAddCourse = () => {
    fetch("http://localhost:8080/enrollCourse", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        idCourse: courseId,
        idStudent: sessionStorage.getItem("idStudent"),
      }),
    }).then(() => updateCourses());
  };
  const handleCompleteTask =()=>{
    if(chosenCourse==="" || chosenTask===""){
      //create here a modal to show that the task or course is not chosen
      <Modal
  open={open}
  onClose={() => setOpen(false)}
>
  <div>
    Please choose a course and task.
  </div>
</Modal>
    }
    else{
      fetch("http://localhost:8080/completeTask", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          idCourse: chosenCourse,
          nameTask: chosenTask,
        }),
      }).then(() => updateCourses());
    }
  }
  console.log(courses)
  return (
    <div>
      <h1>Student: {sessionStorage.getItem("idStudent")}</h1>
      <div style={{ display: "flex", justifyContent: "space-around" }}>
        <div>
        <p>Courses({courses.length})</p>
           <div>
          <div>
            {courses.map((course, index) => (
              <CourseForStudent course={course} index={index} setChosenCourse={setChosenCourse} setChosenTask={setChosenTask}/>
            ))}
          </div>
        </div>
        </div>
        <div>
          <div
            style={{
              display: " flex",
              flexDirection: "column",
              justifyContent: "center",
              paddingTop: " 40px",
            }}
          >
            <Button
              variant="contained"
              style={{ background: "#010101", marginBottom: "10px" }}
              onClick={handleAddCourse}
            >
              Add course
            </Button>
            <TextField
              label="Enter ID of the course"
              variant="outlined"
              value={courseId}
              onChange={handleCourseId}
            />
          </div>
          <div
            style={{
              display: " flex",
              flexDirection: "column",
              justifyContent: "center",
              paddingTop: " 40px",
            }}
          >
            <Button
              variant="contained"
              style={{ background: "#010101", marginBottom: "10px" }}
              onClick={handleCompleteTask}
            >
              Complete task
            </Button>
          </div>
        </div>
      </div>
      <Button
        label="Log out"
        variant="contained"
        style={{ background: "#010101", marginTop: "40px" }}
        onClick={logOut}
      >
        Log out
        </Button>
    </div>
  );
}

export default Student;
