import React from 'react'

function SubItemStudent({item, index, setTask}) {
  const[clicked, setClicked] = useState(false);
  const handleClickTask=(c)=>{
    setClicked(!clicked);
    if(!clicked){
      setTask("");
    }
    else{
      setTask(c.name);
    }
  }
    return (
        <div key={index}>
          <p onClick={handleClickTask}>{index+1}. {item.name}: {item.isCompleted ? "Completed": "Not done"}</p>
        </div>
      )
}

export default SubItemStudent
