import React, { useEffect } from "react";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import ListItem from "../components/ListItem";

function Teacher() {
  const [nameCourse, setNameCourse] = useState("");
  const [nameTask, setNameTask] = useState("");
  const [courses, setCourses] = useState([]);
  const [chosenCourse, setChosenCourse] = useState("");
  const navigate = useNavigate();
  const logOut = () => {
    navigate(-1);
  };
  const handleTextChange = (event) => {
    setNameCourse(event.target.value);
  };
  const handleTaskChange = (event) => {
    setNameTask(event.target.value);
  };
  const updateCourses = () => {
    fetch("http://localhost:8080/teacherCourses", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        idTeacher: sessionStorage.getItem("idTeacher"),
      }),
    })
      .then((res) => res.json())
      .then((data) => setCourses(data));
  };
  useEffect(() => {
    updateCourses();
  }, []);
  const handleAddCourse = () => {
    fetch("http://localhost:8080/createCourse", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        nameCourse: nameCourse,
        idTeacher: sessionStorage.getItem("idTeacher"),
      }),
    }).then(() => updateCourses());
  };
  const handleAddTask = () => {
    fetch("http://localhost:8080/createTask", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        nameTask: nameTask,
        idCourse: chosenCourse,
      }),
    }).then(() => updateCourses());
  };
  return (
    <div>
      <h1>Teacher: {sessionStorage.getItem("idTeacher")}</h1>
      <div style={{ display: "flex", justifyContent: "space-around" }}>
        <div>
          <p>Courses({courses.length})</p>
          <div>
            {courses.map((course, index) => (
              <ListItem course={course} index={index} setChosen={setChosenCourse} />
            ))}
          </div>
        </div>
        <div>
          <div
            style={{
              display: " flex",
              flexDirection: "column",
              justifyContent: "center",
              paddingTop: " 40px",
            }}
          >
            <Button
              variant="contained"
              style={{ background: "#010101", marginBottom: "10px" }}
              onClick={handleAddCourse}
            >
              Add course
            </Button>
            <TextField
              label="Enter name"
              variant="outlined"
              //  value={nameCourse}
              onChange={handleTextChange}
            />
          </div>
          <div
            style={{
              display: " flex",
              flexDirection: "column",
              justifyContent: "center",
              paddingTop: " 40px",
            }}
          >
            <Button
              variant="contained"
              style={{ background: "#010101", marginBottom: "10px" }}
              onClick={handleAddTask}
            >
              Add task
            </Button>
            <TextField
              label="Enter name"
              variant="outlined"
              value={nameTask}
              onChange={handleTaskChange}
            />
          </div>
        </div>
      </div>
      <Button
        label="Log out"
        variant="contained"
        style={{ background: "#010101", marginTop: "40px" }}
        onClick={logOut}
      >
        Log out
      </Button>
    </div>
  );
}

export default Teacher;
